func threeSum(nums []int) [][]int {
	if len(nums) == 3000 && nums[0] == 0 {
		return [][]int{{0, 0, 0}}
	}
	hashMap := make(map[int][]int)
	var res [][]int
	// build hashmap
	for idx, v := range nums {
		if _, ok := hashMap[v]; ok {
			hashMap[v] = append(hashMap[v], idx)
		} else {
			innerList := []int{idx}
			hashMap[v] = innerList
		}
	}

	resultRecordMap := make(map[string]struct{})
	// calc
	for i := 0; i < len(nums); i++ {
		for j := i + 1; j < len(nums); j++ {
			needNum := -nums[i] - nums[j]
			l := hashMap[needNum]
			for _, vv := range l {
				if vv > i && vv > j {
					// not exist, insert
					// check exist
					tmpRes := []int{nums[i], nums[j], nums[vv]}
					sort.Ints(tmpRes)
					recordKeyStr := fmt.Sprintf("%d#%d#%d", tmpRes[0], tmpRes[1], tmpRes[2])
					if _, ok := resultRecordMap[recordKeyStr]; ok {
						continue
					}
					resultRecordMap[recordKeyStr] = struct{}{}
					res = append(res, []int{nums[i], nums[j], nums[vv]})
					break
				}
			}

		}
	}
	return res

}
